      topics = keywords[2:]
      model_outputs = {'topics': topics.T}
      model_outputs['topics'], len(model_outputs['topics'][0])

      # Compute and store metric scores
      diversity_dict[k][n][name] = diversity_metric_dict[k].score(model_outputs)
